	margin: 0 auto;
	border-bottom: 1px solid #BABBC5;
	display: flex;
	justify-content: space-between;
}
// --------------------------------------------------------------------------------------------
.menu {
	position: absolute;
	width: 50px;
	height: 50px;
	font-size: 30px;
	text-align:center;
	border-radius: 100%;
	right:25px;
	top:25px;
}
.menu__ul{
	margin-top: 100px;
	display: flex;
	flex-direction: column;
	align-items: flex-start;
}
.menu-bg {
	@extend .menu;
	pointer-events: none;
	transition: .3s; 
	right:50px;
	top:50px;
	position: fixed;
	
}
.menu-burger {
	@media screen and (min-width: 320px) and (max-width: 900px){
		display: block;
		
	}
	display: none;
	@extend .menu;
	color: white;
	padding-top: 11px;
	cursor: pointer;
	transition: .4s;
	transform-origin: center;
	z-index: 2;
	
}
.menu-items{
	position:absolute;
	font-weight: bold;
	font-size: 40px;
	color: white;
	width: 100%;
	text-align: center;
	opacity:0;
	transition: .4s;
	margin-top:100px;
	transform: translateY(-200%);
	pointer-events:none;
	ul{
		list-style-type: none;
	}
	div {
		transition: 1s;
		opacity: 0;
		margin-top: 0px;
	}
	&.fs {
		transform: translateY(0);
		pointer-events:auto;
		opacity: 1;
		margin: 0 auto;
		background-image: url(/img/background-header.png);
		background-size: cover;
		top: 0;
		left: 0;
		width: 100%;
		height: 100%;
		position: absolute;
